    public String[] parseWeather (String jsonData) {
        JSONObject weather = new JSONObject(jsonData);
        weatherStatus[0] = weather.getJSONArray("weather").getJSONObject(0).getString("main");
        weatherStatus[1] = weather.getJSONArray("weather").getJSONObject(0).getString("description");
        weatherStatus[2] = weather.getJSONArray("weather").getJSONObject(0).getString("icon");
        weatherStatus[3] = String.valueOf(weather.getJSONObject("main").getInt("temp"));
        weatherStatus[4] = String.valueOf(weather.getJSONObject("main").getInt("feels_like"));
        weatherStatus[5] = weather.getJSONObject("sys").getString("country");

        return weatherStatus;
    }

